<?php if (!defined('THINK_PATH')) exit(); if(is_array($NewsList)): $i = 0; $__LIST__ = $NewsList;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$data): $mod = ($i % 2 );++$i;?><tr><td><?php echo ($data["ID"]); ?></td><td><?php echo (msubstr($data["Name"],0,15)); ?></td><td><?php echo (msubstr($data["Overview"],0,20)); ?></td><td><?php echo ($data['ShowHome']==0?'不显示':'首页显示'); ?></td><td><?php echo ($data['Status']==0?'未上线':'在线显示'); ?></td><td><?php echo ($data["EditDate"]); ?></td><td><a href="__URL__/editnews/nid/<?php echo ($data["ID"]); ?>" class="btn btn-grey btn-sm event-more">编辑</a><a href="#"
		class="btn btn-grey btn-sm event-more" onclick="ChangeStatue(this)"><?php echo ($data['Status']==0?'上线':'下线'); ?></a><a href="#" class="btn btn-grey btn-sm event-more"
		onclick="ChangeShow(this)"><?php echo ($data['ShowHome']==0?'首页显示':'不在首页显示'); ?></a><a href="#" class="btn btn-grey btn-sm event-more"
		onclick="DeleteNews(this)">删除</a><p style="display: none" class="pdata"><?php echo (json_encode($data)); ?></p></td></tr><input type="hidden" id="mydatacount" name="name" value="<?php echo ($NewsCount); ?>" /><?php endforeach; endif; else: echo "" ;endif; ?>