<?php if (!defined('THINK_PATH')) exit(); if(is_array($JobList)): $i = 0; $__LIST__ = $JobList;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$data): $mod = ($i % 2 );++$i;?><tr><td><?php echo (msubstr($data["JobName"],0,10)); ?></td><td><?php echo (msubstr($data["joblocation"],0,10)); ?></td><td><?php echo ($data["Pay"]); ?></td><td><?php echo ($data["EditDate"]); ?></td><td><?php echo ($data['Status']==0 ? '未上线' : '在线显示'); ?></td><td><?php echo (mifnull($data["resumisread"],0)); ?>/<?php echo (mifnull($data["resumtotal"],0)); ?></td><td><a href="javascript: void(0);" onclick="lookresum(<?php echo ($data["ID"]); ?>)" class="btn btn-grey" style="padding:4px 6px;"><span class="glyphicon glyphicon-search" style="padding-right:4px;" aria-hidden="true"></span>查看简历</a><a href="#" class="btn btn-grey btn-sm event-more" onclick="EditJob(this)">编辑</a><a href="#" class="btn btn-grey btn-sm event-more" onclick="ChangeStatue(this)"><?php echo ($data['Status']==0?'上线':'下线'); ?></a><a href="#" class="btn btn-grey btn-sm event-more" onclick="DeleteJob(this)">删除</a><p style="display: none" class="pdata"><?php echo (json_encode($data)); ?></p></td></tr><?php endforeach; endif; else: echo "" ;endif; ?><input type="hidden" id="mydatacount" name="name" value="<?php echo ($JobCount); ?>" />